pragma solidity ^0.5.1;

contract WhitePaper {
  uint public userCount = 0;
  uint public totalSections = 5;

  struct User {
      uint id;
      string userAddr;
      uint sectionsUnlocked;
  }

  mapping(uint => User) public users;

  event UserCreated(
    uint id,
    string userAddr
  );

  event UserUpdated(
    uint id, 
    string userAddr, 
    uint sectionsUnlocked
  );

  function createUser(string memory _userAddr) public {
      userCount ++;
      users[userCount] = User(userCount, _userAddr, 1);
      emit UserCreated(userCount, _userAddr);
  }

  function updateUser(uint _id, string memory _userAddr) public {
    User memory _user = users[_id];
    _user.sectionsUnlocked ++;
    users[_id] = _user;

    emit UserUpdated(_id, _userAddr, _user.sectionsUnlocked);
  }
}
